exports.namEve=function(arg2){
var stm=db.prepare('select * from eve where eve=?');
try{var row=stm.all(arg2);
}catch(err){console.log(err);}
return row;
};

exports.allEve=function(){
var stm=db.prepare('select * from eve ');
var row=stm.all();
return row;
};


// === eve run ===
exports.updEve=function(eve,mail){
var stm=db.prepare('update eve set eve=? where email=?');
try{stm.run(eve,mail);
}catch(err){console.log(err);}
};

exports.insEve=function(eve,email,date,des){
var stm=db.prepare('insert into eve values(?,?,?,?) ');
stm.run(eve,email,date,des);
};

exports.delEve=function(){
var stm=db.prepare('delete from eve ');
try{stm.run();
}catch(err){var err=err;console.log(err);}
};


