var Database = require('better-sqlite3');
var db = new Database(__dirname+'/db/cal.db');

// === cal ===
//
exports.mailCal=function(email){
var stm=db.prepare('select * from cal where email=?');
var row=stm.all(email);
return row;
};

exports.namCal=function(arg2){
var stm=db.prepare('select * from cal where cal=?');
try{var row=stm.all(arg2);
}catch(err){console.log(err);}
return row;
};

exports.allCal=function(){
var stm=db.prepare('select * from cal ');
var row=stm.all();
return row;
};


// === cal run ===
exports.updCal=function(cal,mail){
var stm=db.prepare('update cal set cal=? where email=?');
try{stm.run(cal,mail);
}catch(err){console.log(err);}
};

exports.insCal=function(cal,email,date,des){
var stm=db.prepare('insert into cal values(?,?,?,?) ');
stm.run(cal,email,date,des);
};

exports.delCal=function(){
var stm=db.prepare('delete from cal ');
try{stm.run();
}catch(err){var err=err;console.log(err);}
};


